const body = $json;

const prompt = `You are an AI Roadmap Assistant for SkillSync. 
Generate a complete and structured learning roadmap in JSON format.

### Input Details:
Career Field: ${body.careerField}
Current Level: ${body.currentLevel}
Target Level: ${body.targetLevel}
Time Commitment: ${body.timeCommitment} hrs/week
Learning Style: ${body.learningStyle}
Specific Goals: ${body.specificGoals}
Preferred Resources: ${body.preferredResources?.join(", ") || "Any"}

### Output Format:
{
  "roadmap_json": {
    "title": "Frontend Developer Roadmap",
    "overview": {
      "description": "Comprehensive roadmap to become a frontend developer",
      "duration": "6 months",
      "difficulty": 3,
      "outcomes": ["Build responsive websites", "Master React", "Deploy applications"]
    },
    "phases": [
      {
        "id": "phase-1",
        "title": "Basics of Web",
        "description": "Learn HTML, CSS, and JavaScript fundamentals",
        "duration": "2 Weeks",
        "order": 1,
        "color": "#3B82F6",
        "steps": [
          {
            "id": "step-1-1",
            "title": "HTML Fundamentals",
            "description": "Learn HTML structure, semantic elements, and forms",
            "type": "course",
            "duration": "5 days",
            "order": 1,
            "difficulty": 1,
            "skills": ["HTML", "Semantic HTML"],
            "prerequisites": [],
            "resources": [
              {
                "title": "FreeCodeCamp HTML",
                "type": "course",
                "url": "https://www.freecodecamp.org/learn/2022/responsive-web-design/",
                "duration": "20 hours",
                "free": true
              }
            ],
            "projects": [],
            "milestones": []
          }
        ]
      },
      {
        "id": "phase-2",
        "title": "Frontend Frameworks",
        "description": "Master React and state management",
        "duration": "3 Weeks",
        "order": 2,
        "color": "#10B981",
        "steps": [
          {
            "id": "step-2-1",
            "title": "React Basics",
            "description": "Learn React components, props, and hooks",
            "type": "course",
            "duration": "1 week",
            "order": 1,
            "difficulty": 2,
            "skills": ["React", "Components", "Hooks"],
            "prerequisites": ["step-1-1"],
            "resources": [
              {
                "title": "React Official Tutorial",
                "type": "course",
                "url": "https://react.dev/learn",
                "duration": "15 hours",
                "free": true
              }
            ],
            "projects": [
              {
                "title": "Todo App",
                "description": "Build a todo list application",
                "difficulty": 2,
                "estimatedHours": 8,
                "technologies": ["React", "CSS"]
              }
            ],
            "milestones": []
          }
        ]
      }
    ],
    "connections": [
      {
        "from": "phase-1",
        "to": "phase-2",
        "type": "prerequisite"
      }
    ],
    "metadata": {
      "aiModel": "gpt-4o-mini",
      "version": "1.0",
      "tags": ["frontend", "web-development"],
      "industry": "Web Development"
    }
  },
  "roadmap_doc": "Step-by-step journey to become a frontend developer, covering core web fundamentals to advanced frameworks..."
}

IMPORTANT: Return ONLY valid JSON. Do not include markdown code blocks or any text outside the JSON object. Ensure all IDs are unique and the structure matches exactly.";

return [{
  json: {
    ...body,
    formattedPrompt: prompt
  }
}];

// Parse OpenAI response
const aiResponse = $json.choices?.[0]?.message?.content || $json.message?.content || $json.text || "";
let parsed;

try {
  // Try parsing directly
  parsed = JSON.parse(aiResponse);
} catch {
  // Try extracting from markdown code blocks
  const match = aiResponse.match(/```json\n([\s\S]*?)\n```/);
  if (match) {
    parsed = JSON.parse(match[1]);
  } else {
    // Try extracting JSON object
    const jsonMatch = aiResponse.match(/\{[\s\S]*\}/);
    if (jsonMatch) {
      parsed = JSON.parse(jsonMatch[0]);
    } else {
      // Fallback: create structure from text
      parsed = { 
        roadmap_json: {}, 
        roadmap_doc: aiResponse 
      };
    }
  }
}

// Ensure proper structure
if (!parsed.roadmap_json) {
  parsed = {
    roadmap_json: parsed,
    roadmap_doc: parsed.roadmap_doc || "Generated roadmap documentation"
  };
}

return [{
  json: {
    roadmap_json: parsed.roadmap_json,
    roadmap_doc: parsed.roadmap_doc || "",
    generatedAt: new Date().toISOString()
  }
}];
